import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import FormatListBulletedAddIcon from '@mui/icons-material/FormatListBulletedAdd';
import FormatListBulletedIcon from '@mui/icons-material/FormatListBulleted';
import ShareIcon from '@mui/icons-material/Share';
import ChecklistRtlIcon from '@mui/icons-material/ChecklistRtl';
import BuildIcon from '@mui/icons-material/Build';
import LogoutIcon from '@mui/icons-material/Logout';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';

import TabCreate from '../components/TabCreate';
import TabToDo from '../components/TabToDo';


function DashBoard() {
    const navigate = useNavigate();
    const [value, setValue] = useState('1');
    const [open, setOpen] = useState(true);

    const logout = () => {

        localStorage.removeItem('access');
        localStorage.removeItem('refresh');
        navigate('/');
  
    };

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

  const toggleSidebar = () => {
    setOpen(!open);
  };

  return (
     
        <div className="bg-black min-h-screen text-black flex" style={{ position: 'relative' }}>
            <TabContext value={value}>
                <Box sx={{ display: 'flex', width: '100%', flexDirection: 'column' }}>
                    {/* Toolbar fissa in alto */}
                    <Box sx={{ 
                        position: 'sticky', 
                        top: 0, 
                        zIndex: 1100,
                        backgroundColor: '#080808',
                        borderBottom: '1px solid #333',
                        display: 'flex',
                        alignItems: 'center',
                        height: 64,
                        px: 2
                    }}>
                        {!open && (
                            <button onClick={toggleSidebar} style={{ 
                                backgroundColor: 'transparent', 
                                border: 'none', 
                                cursor: 'pointer',
                                marginRight: 16
                            }} title="Open sidebar">
                                <MenuIcon sx={{ color: 'white', fontSize: 35 }} />
                            </button>
                        )}
                        <Box sx={{ flexGrow: 1 }} />
                        <button className="px-4 py-2 rounded hover:bg-blue-500" title="Account">
                            <AccountCircleIcon sx={{ fontSize: 35, color: 'whitesmoke' }} />
                        </button>
                    </Box>

                    {/* Contenuto principale con sidebar e pannelli */}
                    <Box sx={{ display: 'flex', flexGrow: 1 }}>
                        {/* Sidebar (come prima) */}
                        <Box sx={{ 
                            width: open ? 100 : 0, 
                            overflow: 'hidden', 
                            transition: 'width 0.3s ease', 
                            borderRight: open ? 1 : 0, 
                            borderColor: 'divider', 
                            display: 'flex', 
                            flexDirection: 'column', 
                            alignItems: 'center', 
                            backgroundColor: '#080808', 
                            py: 2, 
                            gap: 2,
                            position: 'sticky',
                            top: 64, // Altezza della toolbar
                            height: 'calc(100vh - 64px)', // Altezza viewport meno toolbar
                            alignSelf: 'flex-start'
                        }}>
                            {/* ... il contenuto della tua sidebar rimane uguale ... */}
                        </Box>

                        {/* Pannelli di contenuto (scrollabili) */}
                        <Box sx={{ 
                            flexGrow: 1, 
                            p: 0,
                            overflowY: 'auto',
                            height: 'calc(100vh - 64px)' // Altezza viewport meno toolbar
                        }}>
                            <TabPanel value="1" sx={{ p: 0 }}>
                                <TabToDo/>
                            </TabPanel>
                            <TabPanel value="2" sx={{ p: 0 }}>
                                <TabCreate/>
                            </TabPanel>
                            {/* ... altri TabPanel ... */}
                        </Box>
                    </Box>
                </Box>
            </TabContext>
        </div>
    );
}

export default DashBoard;
