import React, { useEffect, useState } from 'react';



function SendProduct(){

    const [showAlert, setShowAlert] = useState(false);

    const [nomeProd , setNomeProd] = useState('');
    const [descrizione , setDescrizione] = useState('');
    const [qta , setQta] = useState(0);
    const [prezzo , setPrezzo] = useState(0);
    const [fornitore , setFornitore] = useState(0);


    function handleSend(e) {

        e.preventDefault()

        fetch(
        
            'api/insert_product/',
            
            {
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify({nomeProd , descrizione , qta , prezzo , fornitore})

            }

        )
        .then( res => 
            {
                if(!res.ok){
                    throw new Error('Si e verificato un problema durante l invio')
                }
                return res.json()
            }

        )
        .then( data =>
            {
                setNomeProd('');
                setDescrizione('');
                setQta(0);
                setPrezzo(0);
                setFornitore(0);

                window.location.reload()

            }

        )
        .catch(err => setShowAlert(err.message));

    };



    return(
        <div>
 
            <div className=' text-left'>

                <div className='py-10 px-36'>
                    
                    <h1 className=' text-2xl font-light'>REGISTRA PRODOTTO</h1>

                </div>
            

            </div>


            <form  onSubmit={handleSend}>

        

                <div className='text-center text-3xl grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center py-40 text-black'>


                    <div className=''>
                        <input type="text" placeholder='Nome prodotto' value={nomeProd} onChange={e => setNomeProd(e.target.value)}/>
                    </div>
                    
                    <div className=''>
                        
                         <input type="text" placeholder='Descrizione' value={descrizione} onChange={e => setDescrizione(e.target.value)}/>   
                    </div>
                    
                    <div className=''>

                       <input type="number" placeholder='quantita' value={qta} onChange={e => setQta(Number(e.target.value))}/>
                    </div>

                            

                    <div className=''>

                      <input type="number" placeholder='Prezzo' value={prezzo} onChange={e => setPrezzo(Number(e.target.value))}/>
                    </div>

                    
                    <div className=''>
                        <input type="number" placeholder='Fornitore' value={fornitore} onChange={e => setFornitore(Number(e.target.value))}/>
                    </div>
                    

                    
                    <div className=' font-light text-xl '>
                        <button className='w-full h-14 bg-blue-600  rounded-md' type="submit">Invio</button>
                    </div>

                </div>
                

            </form>



        </div>
    );

}

export default SendProduct;
