import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import TextField from '@mui/material/TextField';
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';

function SendProduct(){

    const [showAlert, setShowAlert] = useState(false);

    const [nomeProd , setNomeProd] = useState('');
    const [descrizione , setDescrizione] = useState('');
    const [qta , setQta] = useState(0);
    const [prezzo , setPrezzo] = useState(0);
    const [fornitore , setFornitore] = useState(0);


    const handleSend = (e) => {

        e.preventDefault()

        fetch(
        
            '/api/insert_product/',
            
            {
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify({nomeProd , descrizione , qta , prezzo , fornitore})

            }

        )
        .then( res => 
            {
                if(!res.ok){
                    throw new Error('Si e verificato un problema durante l invio')
                }
                return res.json()
            }

        )
        .then( data =>
            {
                setNomeProd('');
                setDescrizione('');
                setQta(0);
                setPrezzo(0);
                setFornitore(0);

                window.location.reload()

            }

        )
        .catch(err => setShowAlert(err.message));

    };



    return(
        <div>
 
            <div className=' text-left'>

                <div className='py-10 px-36'>
                    
                    <h1 className=' text-2xl font-light'>REGISTRA PRODOTTO</h1>

                </div>
            

            </div>


            <form action="submit" onSubmit={handleSend}>

             
                


                <div className='text-center text-3xl grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center py-40'>


                    <div className='w-4/6'>
                        <TextField   
                            sx={{
                                width : 550,
                                '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                                '& .MuiInputLabel-root': { color: 'white' }, // label
                                '& .MuiOutlinedInput-root': {
                                '& fieldset': { borderColor: 'white' },
                                '&:hover fieldset': { borderColor: 'white' },
                                '&.Mui-focused fieldset': { borderColor: 'white' },
                                },
                                '& label.Mui-focused': { color: 'white' }
                            }}  
                            id="outlined-multiline-flexible" label="Nome prodotto" multiline maxRows={4}
                        />
                    </div>
                    
                    <div className='w-4/6'>
                        
                        <TextField   
                            sx={{
                                width : 550,
                                '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                                '& .MuiInputLabel-root': { color: 'white' }, // label
                                '& .MuiOutlinedInput-root': {
                                '& fieldset': { borderColor: 'white' },
                                '&:hover fieldset': { borderColor: 'white' },
                                '&.Mui-focused fieldset': { borderColor: 'white' },
                                },
                                '& label.Mui-focused': { color: 'white' }
                            }}  
                            id="outlined-multiline-flexible" label="Descrizione" multiline maxRows={4}
                        
                        
                        />

                    </div>
                    
                    <div className='w-4/6'>

                        <TextField   
                            sx={{
                                width : 550,
                                '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                                '& .MuiInputLabel-root': { color: 'white' }, // label
                                '& .MuiOutlinedInput-root': {
                                '& fieldset': { borderColor: 'white' },
                                '&:hover fieldset': { borderColor: 'white' },
                                '&.Mui-focused fieldset': { borderColor: 'white' },
                                },
                                '& label.Mui-focused': { color: 'white' }
                            }}  
                            id="outlined-multiline-flexible" label="Quantita" type='number' multiline maxRows={4}
                        />

                    </div>

                            

                    <div className='w-4/6'>

                        <TextField   
                            sx={{
                                width : 550,
                                '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                                '& .MuiInputLabel-root': { color: 'white' }, // label
                                '& .MuiOutlinedInput-root': {
                                '& fieldset': { borderColor: 'white' },
                                '&:hover fieldset': { borderColor: 'white' },
                                '&.Mui-focused fieldset': { borderColor: 'white' },
                                },
                                '& label.Mui-focused': { color: 'white' }
                            }}  
                            id="outlined-multiline-flexible" label="Prezzo" type='number' multiline maxRows={4}
                        />

                    </div>

                    
                    <div className='w-4/6'>
                        
                        <TextField   
                            sx={{
                                width : 550,
                                '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                                '& .MuiInputLabel-root': { color: 'white' }, // label
                                '& .MuiOutlinedInput-root': {
                                '& fieldset': { borderColor: 'white' },
                                '&:hover fieldset': { borderColor: 'white' },
                                '&.Mui-focused fieldset': { borderColor: 'white' },
                                },
                                '& label.Mui-focused': { color: 'white' }
                            }}  
                            id="outlined-multiline-flexible" label="ID Fornitore" type='number' multiline maxRows={4}
                        />

                    </div>
                    

                    
                    <div className=' font-light text-xl w-4/6'>
                        <button className='w-full h-14 bg-blue-600  rounded-md' type='submit'>Invio</button>
                    </div>

                </div>
                

            </form>



        </div>
    );

}

export default SendProduct;
