import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from 'react';

function App() {

  const [message , setMessage] = useState(null);
  const [num , setNum] = useState(0)

  function count(){
    let operationINCR = num+1
    setNum(operationINCR);
  };

  useEffect(()=>{

    fetch('http://127.0.0.1:8000/api/search_product/2/')
    .then(res => res.json())
    .then(data => setMessage(data))
    .catch(err => setMessage('Errore : ' +err.message));


  },[]);



  return (
    <div className="App">

      <header className="App-header">

      <h2 class="text-xxl text-justify text-red-400">Dettagli API </h2> <br/>

      <p class="text-xl text-justify">

        -Fornitore : {message?.cliente?.nome_cliente} <br/>
        -Prodotto : {message?.nome_prodotto} <br/>
        -Prezzo : {message?.prezzo} <br/>

      </p>

      <p class="text-xl text-justify">Acquisti : {num}</p>

      <div class="py-6"></div>

      <button class="w-36 bg-cyan-900 text-white text-xl" onClick={count} >Compra</button>


      </header>

    </div>
  );
}

export default App;
