import React, { useEffect, useState } from 'react';

function User(){

    const token_access = localStorage.getItem('access');
    const [usr , setUsr] = useState('');

    useEffect(() => {

        fetch(
            'api/myprofile/', 
            {
                headers: {
                    'Authorization' : `Bearer ${token_access}`,
                }
            }
        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualcosa è andato storto')
                }
                return res.json();
            }
        )
        .then(data => setUsr(data))
        .catch(err => console.log(err.message))

    });

    return(

        <div className="bg-gray-800  border border-gray-700 p-4 shadow-sm min-h-screen">
            <div className="flex items-center justify-between">
                <div>
                    <p className="font-medium text-white">@{usr?.username}</p>
                    <p className="text-sm text-gray-400">Registrato il {usr?.date_joined}</p>
                </div>
            </div>
        </div>
    );


}

export default User;
