import React, { useEffect, useState } from 'react';



function Note(){

    const [content , setContent] = useState([])

   // const [id_note , setIdNote] = useState(0)
    const [titolo , setTitolo] = useState('')
    const [contenuto , setContenuto] = useState('')
    const [message , setMessage] = useState('')

    useEffect(()=>{

        fetch('/api/note/')
        .then(res => res.json())
        .then(data => setContent(data))  
        .catch(err =>  setContent([]));

    },[]);


    function creaNota(e){

        e.preventDefault();

        fetch
        (
            '/api/createNote/' , 
            {
                method : 'POST' , 
                headers: {'Content-Type' : 'application/json'},
                body : JSON.stringify({titolo,contenuto}), 
            }
        )
        .then(res => 
            {
                if (!res.ok){
                    throw new Error('Errore nella creazione della nota')
                }
                return res.json();
            }
        )
        .then(data => {
                setTitolo('');
                setContenuto('');
                window.location.reload();
            }
        
        )
        .catch(err => setMessage(err.message))
    }



    return(

        <div class="bg-slate-800">
            
            <div class="text-center py-24">
                
                <h1 class="text-2xl text-white  font-light">Practice 2</h1>
                
                <p class="text-white">

                    {content.map(content => ( <li key={content.id_note}>{content.titolo} ----- {content.contenuto}</li>))} 

                </p>
               
            </div>

            <div className='text-center text-white py-28'>
                <form onSubmit={creaNota}>
                    
                  

                    <p>Titolo : </p>
                    <input className='text-black' type="text" value={titolo}  onChange={e => setTitolo(e.target.value)}/>
                    
                    <p>Contenuto : </p>
                    <input className='w-72 text-black' type="text" value={contenuto} onChange={e => setContenuto(e.target.value)} />
                    
                    <br />

                    <div className='py-5'></div>

                    <button className='bg-cyan-700 w-72' type='submit' >Crea la nota</button> 
                    {message && <p>{message}</p>}
                </form>

            </div>





        </div>
    )
}

export default Note;
