import React, { useEffect, useState } from 'react';

function User(){

    const token_access = localStorage.getItem('access');
    const [usr , setUsr] = useState('');

    useEffect(() => {

        fetch(
            'api/myprofile/', 
            {
                headers: {
                    'Authorization' : `Bearer ${token_access}`,
                }
            }
        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualcosa è andato storto')
                }
                return res.json();
            }
        )
        .then(data => setUsr(data))
        .catch(err => console.log(err.message))

    });

    return(

        <div className="bg-gray-800 rounded-lg border border-gray-700 p-4 shadow-sm min-h-screen">
            <div className="flex items-center justify-between">
                <div>
                <p className="font-medium text-white">@username</p>
                <p className="text-sm text-gray-400">Registrato il 12/05/2023</p>
                </div>
                {/* Icona opzionale */}
                <div className="text-gray-400">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 12h.01M12 12h.01M19 12h.01M6 12a1 1 0 11-2 0 1 1 0 012 0zm7 0a1 1 0 11-2 0 1 1 0 012 0zm7 0a1 1 0 11-2 0 1 1 0 012 0z" />
                </svg>
                </div>
            </div>
        </div>
    );


}

export default User;
