import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import TextField from '@mui/material/TextField';
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';

function SendProduct(){

    const [showAlert, setShowAlert] = useState(false);

    const handleSend = () => {
        setShowAlert(true);
    };

    const SimpleAlert = () => (
        <Alert icon={<CheckIcon fontSize="inherit" />} severity="success">
            Here is a gentle confirmation that your action was successful.
        </Alert>
    );

    return(
        <div className='text-center text-3xl grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center py-52'>

            

            <div>
                <TextField   
                    sx={{
                        width : 600,
                        '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                        '& .MuiInputLabel-root': { color: 'white' }, // label
                        '& .MuiOutlinedInput-root': {
                        '& fieldset': { borderColor: 'white' },
                        '&:hover fieldset': { borderColor: 'white' },
                        '&.Mui-focused fieldset': { borderColor: 'white' },
                        },
                        '& label.Mui-focused': { color: 'white' }
                    }}  
                    id="outlined-multiline-flexible" label="Nome prodotto" multiline maxRows={4}
                />
            </div>
            
            <div>
                
                <TextField   
                    sx={{
                        width : 600,
                        '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                        '& .MuiInputLabel-root': { color: 'white' }, // label
                        '& .MuiOutlinedInput-root': {
                        '& fieldset': { borderColor: 'white' },
                        '&:hover fieldset': { borderColor: 'white' },
                        '&.Mui-focused fieldset': { borderColor: 'white' },
                        },
                        '& label.Mui-focused': { color: 'white' }
                    }}  
                    id="outlined-multiline-flexible" label="Descrizione" multiline maxRows={4}
                />

            </div>
            
            <div>

                <TextField   
                    sx={{
                         width : 600,
                        '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                        '& .MuiInputLabel-root': { color: 'white' }, // label
                        '& .MuiOutlinedInput-root': {
                        '& fieldset': { borderColor: 'white' },
                        '&:hover fieldset': { borderColor: 'white' },
                        '&.Mui-focused fieldset': { borderColor: 'white' },
                        },
                        '& label.Mui-focused': { color: 'white' }
                    }}  
                    id="outlined-multiline-flexible" label="Quantita" type='number' multiline maxRows={4}
                />

            </div>

            

            <div>

                <TextField   
                    sx={{
                         width : 600,
                        '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                        '& .MuiInputLabel-root': { color: 'white' }, // label
                        '& .MuiOutlinedInput-root': {
                        '& fieldset': { borderColor: 'white' },
                        '&:hover fieldset': { borderColor: 'white' },
                        '&.Mui-focused fieldset': { borderColor: 'white' },
                        },
                        '& label.Mui-focused': { color: 'white' }
                    }}  
                    id="outlined-multiline-flexible" label="Prezzo" type='number' multiline maxRows={4}
                />

            </div>

            
            <div>
                
                <TextField   
                    sx={{
                         width : 600,
                        '& .MuiInputBase-input': { color: 'white' }, // testo digitato
                        '& .MuiInputLabel-root': { color: 'white' }, // label
                        '& .MuiOutlinedInput-root': {
                        '& fieldset': { borderColor: 'white' },
                        '&:hover fieldset': { borderColor: 'white' },
                        '&.Mui-focused fieldset': { borderColor: 'white' },
                        },
                        '& label.Mui-focused': { color: 'white' }
                    }}  
                    id="outlined-multiline-flexible" label="ID Fornitore" type='number' multiline maxRows={4}
                />

            </div>
                    
            <div className=' font-light text-xl w-96'>
                <button className='w-full h-14 bg-blue-600  rounded-md ' onClick={handleSend}>Invio</button>
            </div>


        </div>
    );

}

export default SendProduct;
