import { useState } from 'react';

export default function EditableTitle({ defaultTitle }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(defaultTitle);

  const handleBlur = () => {
    setIsEditing(false);
    // Puoi anche inviare il nuovo titolo al back-end qui
    console.log("Titolo aggiornato:", title);
  };

  useEffect(()=>{
  
          fetch(
              'api/tasks/', 
              {
  
                  headers : {
              
                      'Authorization' : `Bearer ${token_access}`,
                  }
                 
              }
          )
          .then(res => 
              {
                  if(!res.ok){
                      throw new Error('Qualcosa e andato storto')
                  }
                  return res.json()
              }
          )
          .then(
              data => 
              {
                  setTask(data)
                
              }
          )
          .catch(err => console.log(err.message))
  
      },[]);


  return (
    <div>
      {isEditing ? (
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          onBlur={handleBlur}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              handleBlur();
            }
          }}
          autoFocus
          style={{ backgroundColor: 'white', color: 'black' }}
        />
      ) : (
        <h1 onClick={() => setIsEditing(true)} style={{ cursor: 'pointer' }}>
          {title}
        </h1>
      )}
    </div>
  );
}
