import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel'
import { useNavigate } from 'react-router-dom';


function Dashboard(){
    
    const navigate  = useNavigate()

    const [value, setValue] = React.useState('1');

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };
    

    const logout = () =>{

        navigate('/')


    }

    
    return(

        <div className=''>


            <div className='grid grid-cols-1 text-center  min-h-screen'>
                <Box sx={{ width: '100%', typography: 'body1', height : '100%' , bgcolor: '#1e293b' }}>
                    <TabContext value={value}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' , color:'white'}}>
                            <TabList onChange={handleChange} aria-label="lab API tabs example">
                                <Tab textColor ="inherit" sx={{  color: 'white','&.Mui-selected': {color: '#00e5ff',},'&.Mui-focusVisible': {outline: '2px solid #00e5ff', borderRadius: '4px',},}} label="GESTIONE PRODOTTO" value="1" />
                                <Tab textColor="inherit"  sx={{  color: 'white','&.Mui-selected': {color: '#00e5ff',},'&.Mui-focusVisible': {outline: '2px solid #00e5ff', borderRadius: '4px',},}} label="GESTIONE FORNITORE" value="2" />
                                <Tab textColor="inherit"  sx={{  color: 'white','&.Mui-selected': {color: '#00e5ff',},'&.Mui-focusVisible': {outline: '2px solid #00e5ff', borderRadius: '4px',},}} label="GESTIONE SOCIETA" value="3" />
                                <Tab textColor="inherit"  sx={{  color: 'white','&.Mui-selected': {color: '#00e5ff',},'&.Mui-focusVisible': {outline: '2px solid #00e5ff', borderRadius: '4px',},}} label="GESTIONE FATTURA" value="3" />
                                <button className='bg-blue-800 w-28 rounded-md' onClick={logout}>LOGOUT</button>
                            </TabList>
                        </Box>
                        <TabPanel sx={{ color: 'white' }} value="1">Item One</TabPanel>
                        <TabPanel sx={{ color: 'white' }} value="2">Item Two</TabPanel>
                        <TabPanel sx={{ color: 'white' }} value="3">Item Three</TabPanel>
                        
                    </TabContext>
                </Box>


                <div className="text-center text-3xl grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center py-52 ">

                <div className="font-light bg-white w-40 rounded-md py-2">
                    <Link to="/note">POST  <br/> + <br/> GET '[ ]' </Link>
                </div>
                <div className="font-light bg-white w-40 rounded-md py-2">
                    <Link to="/persona">GET</Link>
                </div>
                <div className="font-light bg-white w-40 rounded-md py-2">
                    <Link to="/prodotto">DELETE</Link>
                </div>

            </div>

            </div>

            




        </div>

    );

}

export default Dashboard;
