import React, { useEffect, useState } from 'react';


function Company(){

    const [societa , setSocieta] = useState([]);

    const [id_societa , setIdSocieta] = useState(0);

    {/*FOR POST METHOD*/}
    const [nome , setNome] = useState('');
    const [citta , setCitta] = useState('');
    const [paese , setPaese] = useState('');


    {/*FOR PATCH METHOD*/}
    const [nome_societa , setNomeSocieta] = useState('');
    const [citta_societa , setCittaSocieta] = useState('');
    const [paese_societa , setPaeseSocieta] = useState('');



    {/*CHIAMATA GET*/}
    const refresh = () =>{
      
        fetch('api/companies/')
        .then(res => res.json())    
        .then(data => setSocieta(data))
        .catch(err => setSocieta([]))   
       
    };

    {/*REFRESH IN TEMPO REALE RICHIAMATO NEI VARI METODI POST DELETE PATCH ECC..*/}
    useEffect(() =>{

        refresh();

    },[]);


    const payload = {};

    if(nome_societa.trim() !== '')payload.nome_societa = nome_societa;
    if(citta_societa.trim() !== '')payload.citta_societa = citta_societa;
    if(paese_societa.trim() !== '')payload.paese_societa = paese_societa;


    function modifyCompany(e){

        e.preventDefault();

        fetch
        (


        )


    }




    function handleCompany(e){
        e.preventDefault()


        fetch
        (
            'api/insert_companies/',
            {
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},

                body : JSON.stringify
                (
                    {
                        nome_societa : nome,
                        citta : citta,
                        paese : paese
                    }
                )

            }
        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Impossibile inserire la societa nella base dati')
                }
                return res.json()
            }
            
        )
        .then(data => 
            {
                setNome('');
                setCitta('');
                setPaese('');

                refresh();
            }
        )
        .catch(err => console.log(err))

    }


    const handleDelete = (id_societa) =>{

        fetch
        (
            `api/del_company/${id_societa}/`,

            {
                method:'DELETE',
            }

        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Impossibile eliminare la riga si e verificato un problema')
                }
                refresh();
                return res.json()
            }
        )
        .catch(err => console.log(err))
        
        
    };
  

 
    return(

        <div>
            
            <div>
                <table className='bg-gray-900 w-full rounded-md'>

                    <thead>
                        <tr>

                            <th className='text-green-400'>ID SOCIETA</th>
                            <th className='text-green-400'>NOME SOCIETA</th>
                            <th className='text-green-400'>CITTA</th>
                            <th className='text-green-400'>PAESE</th>

                        </tr>

                    </thead>
                    
                    <tbody>

                        { Array.isArray(societa) && societa.map(societa =>(
                            
                            <tr key={societa.id_societa}>
                                <td>{societa.id_societa}</td>
                                <td>{societa.nome_societa}</td>
                                <td>{societa.citta}</td>
                                <td>{societa.paese}</td>
                                <td onClick={() => handleDelete(Number(societa.id_societa))}><button >‚ùå</button></td> {/*SCRIVERE API PER IL DELETE E MAPPARE IL BUTTON CON LA PK onClick(delete(societa.id_societa))*/}
                            </tr>

                        ))}


                    </tbody>



                </table>

                
            </div>

            <div className='py-10  grid grid-cols-1 md:grid-cols-3 gap-4 justify-center items-center content-center'>


                <div>
                    <form action="" onSubmit={handleCompany}>
                        <div>
                            <label htmlFor="" className='px-2'>societa</label>
                            <input type="text" value={nome} onChange={e => setNome(e.target.value)} className='w-72 h-7 px-2 text-black'/>
                        </div>
                    

                        <div className='py-5'>
                            <label htmlFor="" className='px-5'>citta</label>
                            <input type="text" value={citta} onChange={e => setCitta(e.target.value)} className='w-72 h-7 px-2 text-black' />
                        </div>

                        <div>
                            <label htmlFor="" className='px-3'>paese</label>
                            <input type="text" value={paese} onChange={e => setPaese(e.target.value)} className='w-72 h-7 px-2 text-black'/>
                        </div>

                        <div  className='py-5'>
                            <label htmlFor="" className='px-3'>PUSH</label>
                            <button type='submit' className='bg-green-900 w-72 h-10 rounded-md hover:bg-green-500'>SUBMIT</button>

                        </div>

                    </form>
                    
                    
                </div>
                
              




            </div>
            


        </div>

    );

}

export default Company
