import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
//import Shop from './pages/Shop';
//import { BrowserRouter, Routes, Route, Link } from "react-router-dom"; /api/search_product/2/
import { TextField, Button, Box, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

function App() {

  const [usr, setUsr] = useState('');
  const [pwd, setPassword] = useState('');
  const navigate = useNavigate();


  const hadlelogin = async (e) => {

    e.preventDefault();

    const res = await fetch
    (
      'auth/token/',
      {

        method : 'POST',   
        headers : {'Content-Type' : 'application/json'},
        body : JSON.stringify({username : usr , password : pwd})

      } 
    )

    const data = await res.json();

    if(res.ok){
      localStorage.setItem('access' , data.access);
      localStorage.setItem('refresh' , data.refresh);
      alert('logged in')
      navigate('/dashboard');
    }
    else{
      alert('Invalid credentials')
    }

  };

  return (

    <div className='bg-gradient-to-r from-blue-900 to-slate-900 min-h-screen'>


      <div className='text-center  text-white py-5'>
        <h1 className=' font-light text-6xl'>ODA</h1>
        <h2 className=' font-light text-xl px-10'>A smarter way to manage your documents live, secure, and optimized.</h2>
      </div>


      <div  className="text-center text-3xl grid grid-cols-1 md:grid-cols-1 gap-4 justify-items-center items-center py-1">

        <div className=''>

          <Box component="form" onSubmit={hadlelogin} sx={{ display: 'flex', flexDirection: 'column', maxWidth: 400, margin: 'auto', mt: 10 }} className='bg-white w-80 md:w-96 px-4 py-4 rounded-md'>

            <Typography variant="h5" mb={2} className='text-black' >Login</Typography>

            <TextField  label="Username" type="username"  value={usr} onChange={(e) => setUsr(e.target.value)} margin="normal" required/>
            <TextField label="Password" type="password" value={pwd} onChange={(e) => setPassword(e.target.value)} margin="normal" required/>


            <Button type="submit" variant="contained" sx={{ mt: 2 }}>
             Accedi
            </Button>
          </Box>

        </div>



      </div>



      <div className='py-0'></div>

    </div>

  );
}

export default App;
