import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';


function Prodotto(){

    const [message , setMessage] = useState('');
 
    const [nome_prodotto , setNomeProdotto] = useState('');
    const [descrizione , setDescrizione] = useState('');
    const [qta , setQta] = useState(0);
    const [prezzo , setPrezzo] = useState(0);
    const [cliente , setCliente] = useState(0);



    function inserisci_prodotto(e){

        e.preventDefault()
        
        fetch
        (
            'api/insert_product/', 
            {
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify({nome_prodotto, descrizione , qta , prezzo, cliente})
            }

        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Errore durante l invio')
                }
                return res.json()

            }
        )
        .then(data => 

            {
                setNomeProdotto('')
                setDescrizione('')
                setQta(0)
                setPrezzo(0)
                setCliente(0)

                window.location.reload()
            }

        )
        .catch(err => setMessage(err.message))
    }



    return(

        <div >
            
            <div className='py-20 text-center'>

                <form action="submit"  onSubmit={inserisci_prodotto}>
                    
                    <label className='text-white'>Prodotto : </label>
                    <input type="text" value={nome_prodotto} onChange={e=>setNomeProdotto(e.target.value)} />

                    <label className='text-white'>Descrizione : </label>
                    <input type="text" value={descrizione} onChange={e => setDescrizione(e.target.value)}/>

                    <label className='text-white'>Quantita : </label> 
                    <input type="number" value={qta} onChange={e => setQta(e.target.value)}/>

                    <label className='text-white'>Prezzo : </label>
                    <input type="number" step="0.01" />

                    <label className='text-white'>Id fornitore : </label>
                    <input type="number" value={cliente} onChange={e => setCliente(e.target.value)}/>

                </form>

                <div className='py-20'></div>

                <button type='submit' className='bg-white w-80'>Invia</button>
                {message && <p className='text-white'>{message}</p>}


            </div>
      


        </div>

    );
}

export default Prodotto;
