import React, { useEffect, useState } from 'react';
import CircleIcon from '@mui/icons-material/Circle';
function TabToDo(){

    const [task , setTask] = useState([]);
    const [stato , setStato] = useState(null);

    useEffect(()=>{

        fetch('api/tasks/')
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualcosa e andato storto')
                }
                return res.json()
            }
        )
        .then(
            data => 
            {
                setTask(data)
              
            }
        )
        .catch(err => console.log(err.message))

    },[]);


    return(
        <div className="w-auto grid grid-cols-1 md:grid-cols-4 gap-4 md:grid-rows-2 items-center justify-center">

            <div className="bg-black h-16 md:col-span-4"></div>

            {Array.isArray(task) && task.map(task => (
                <div key={task.id} className="py-4 md:py-6 w-full md:w-96 bg-slate-300 h-auto rounded-md">
                    <div className='flex flex-col items-center p-2'>
                        <h1 className="text-lg font-medium">Titolo: {task.titolo}</h1>
                    </div>
                </div>
            ))}    

        </div>
    );
}

export default TabToDo;
