import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';


function LogIn(){

    const [username , setUsername] = useState('');
    const [password , setPassword] = useState('');

    const navigate = useNavigate();


    const hanleLogin = async (e) =>{

        e.preventDefault()

        const res = await fetch
        (

            'auth/token/',
            {
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify({username:username , password : password})
            }
            
        )

        const data = await res.json();

        if(res.ok){

            localStorage.setItem('access' , data.access);
            localStorage.setItem('refresh' , data.refresh);
            alert('accesso consentito')
            navigate('/dashboard')
        }
        else{
            alert('credenziali errate')
        }

    };
 

    return(

        <div className="min-h-screen bg-black flex flex-col">
          
            <div className='text-center text-white text-7xl py-10 font-thin sticky top-0 z-50 bg-black border-b border-gray-800'>
                BOOST-2-DO
            </div>
            
          
            <div className="flex flex-1 items-center justify-center bg-black p-4">
                <div className="bg-slate-800 p-8 rounded-xl w-full max-w-md border-b-2 border-t-2 border-blue-700 shadow-lg">
                    <form onSubmit={hanleLogin} className="flex flex-col gap-6">
                        <div className="text-center">
                            <h1 className="text-white font-thin text-2xl mb-6">LOGIN</h1>
                        </div>

                        <div className="flex flex-col gap-1">
                            <label htmlFor="username" className="text-white text-sm font-light">Username</label>
                            <input 
                                value={username} 
                                onChange={e => setUsername(e.target.value)}  
                                type="text" 
                                id="username" 
                                className="px-4 py-3 rounded-md bg-gray-900 text-white border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
                            />
                        </div>

                        <div className="flex flex-col gap-1">
                            <label htmlFor="password" className="text-white text-sm font-light">Password</label>
                            <input 
                                value={password} 
                                onChange={e => setPassword(e.target.value)}  
                                type="password" 
                                id="password" 
                                className="px-4 py-3 rounded-md bg-gray-900 text-white border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
                            />
                        </div>

                        <div className="flex flex-row justify-between pt-4">
                            <button 
                                type="button"
                                className="px-6 py-2 rounded-md bg-blue-600 text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all w-40"
                            >
                                LogIn
                            </button>
                            
                            <button 
                                type='submit' 
                                className="px-6 py-2 rounded-md bg-gray-700 text-white hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500 transition-all w-40"
                            >
                                Register
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
      

    );

}

export default LogIn;
