import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
//import Shop from './pages/Shop';
//import { BrowserRouter, Routes, Route, Link } from "react-router-dom"; /api/search_product/2/
import { TextField, Button, Box, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

function App() {

  const [usr, setEmail] = useState('');
  const [pwd, setPassword] = useState('');
  const navigate = useNavigate();


  function fakeLogin() { //solo perche il form html lo richiede

    if (usr === "root@gmail.com" && pwd === "root") {
      navigate('/prodotto');
    }
  }

  return (

    <div className='bg-gradient-to-r from-blue-900 to-slate-900 min-h-screen'>


      <div className='text-center  text-white py-10'>
        <h1 className=' font-light text-5xl'>ODA</h1>
        <h2 className=' font-light text-xl'>A smarter way to manage your documents live, secure, and collaborative.</h2>
      </div>


      <div  className="text-center text-3xl grid grid-cols-1 md:grid-cols-1 gap-4 justify-items-center items-center py-10">

        <div className=''>

          <Box component="form" onSubmit={fakeLogin} sx={{ display: 'flex', flexDirection: 'column', maxWidth: 400, margin: 'auto', mt: 10 }} className='bg-white w-80 md:w-96 px-4 py-4 rounded-md'>

            <Typography variant="h5" mb={2} className='text-black' >Login</Typography>

            <TextField  label="Email" type="email"  value={usr} onChange={(e) => setEmail(e.target.value)} margin="normal" required/>
            <TextField label="Password" type="password" value={pwd} onChange={(e) => setPassword(e.target.value)} margin="normal" required/>


            <Button type="submit" variant="contained" sx={{ mt: 2 }}>
             Accedi
            </Button>
          </Box>

        </div>



      </div>

      <div className="text-center text-3xl grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center py-52 ">

        <div className="font-light bg-white w-40 rounded-md py-2">
          <Link to="/note">POST  <br/> + <br/> GET '[ ]' </Link>
        </div>
        <div className="font-light bg-white w-40 rounded-md py-2">
          <Link to="/persona">GET</Link>
        </div>
        <div className="font-light bg-white w-40 rounded-md py-2">
          <Link to="/prodotto">DELETE</Link>
        </div>
      </div>

      <div className='py-0'></div>

    </div>

  );
}

export default App;
