import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';


function Prodotto(){

    const [message , setMessage] = useState('');
 
    const [nome_prodotto , setNomeProdotto] = useState('');
    const [descrizione , setDescrizione] = useState('');
    const [qta , setQta] = useState(0);
    const [prezzo , setPrezzo] = useState(0);
    const [cliente , setCliente] = useState(0);



    function inserisci_prodotto(e){

        e.preventDefault()
        
        fetch
        (
            '/api/insert_product/', 
            {
                method : 'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify({nome_prodotto, descrizione , qta , prezzo, cliente})
            }

        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Errore durante l invio')
                }
                return res.json()

            }
        )
        .then(data => 

            {
                setNomeProdotto('')
                setDescrizione('')
                setQta(0)
                setPrezzo(0)
                setCliente(0)

                window.location.reload()
            }

        )
        .catch(err => setMessage(err.message))
    }



    return(

        <div className='text-white'>
            
            <div>

                <form action="submit"  onSubmit={inserisci_prodotto}>
                    <label>Prodotto : </label>
                <input type="text" value={nome_prodotto} onChange={e=>setNomeProdotto(e.target.value)} />

            </form>

            </div>
      


        </div>

    );
}

export default Prodotto;
