import React, { useEffect, useState } from 'react';


function Company(){

    const [refresh , setRefresh] = useState(false);

    const [societa , setSocieta] = useState([]);


    const loading = () =>{
        setRefresh(prev => !prev)
    }

    useEffect(()=>{

        fetch('api/companies/')
        .then(res => res.json())    
        .then(data => setSocieta(data))
        .catch(err => setSocieta([]))   
    },[loading])


    return(

        <div>
            
            
            <table className='bg-gray-900 w-full rounded-md'>

                 <thead>
                    <tr>

                        <th className='text-green-400'>ID SOCIETA</th>
                        <th className='text-green-400'>NOME SOCIETA</th>
                        <th className='text-green-400'>CITTA</th>
                        <th className='text-green-400'>PAESE</th>

                    </tr>
                </thead>
                
                <tbody>
                    { Array.isArray(societa) && societa.map(societa =>(
                        
                        <tr key={societa.id_societa}>
                            <td>{societa.id_societa}</td>
                            <td>{societa.nome_societa}</td>
                            <td>{societa.citta}</td>
                            <td>{societa.paese}</td>

                        </tr>

                    ))}


                </tbody>



            </table>

        </div>

    );

}

export default Company
