import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from 'react';



function App() {

  const [message , setMessage] = useState(null);
  const [num , setNum] = useState(0)
  const [costo , setCosto] = useState(0)


  useEffect(()=>{

    fetch('http://127.0.0.1:8000/api/search_product/2/')
    .then(res => res.json())
    .then(data => setMessage(data))
    .catch(err => setMessage('Errore : ' +err.message));


  },[]);



  function count(){

    let operationINCR = num+1;

    console.log(operationINCR)

    setNum(operationINCR);

    return operationINCR
  };



  function compra(){

    let numeroProdotti = count();
    let operazione = 0;

    if(numeroProdotti !== 1){

      numeroProdotti = numeroProdotti-1
      console.log(numeroProdotti)
    }
    else{
      operazione = numeroProdotti * message?.prezzo;

      setCosto(operazione)
      setNum(0)
    }

    return operazione
  }



  return (
    <div className="App">

      <header className="App-header">

      <h2 class="text-xxl text-justify text-red-400">Dettagli API </h2> <br/>

      <p class="text-xl text-justify">

        -Fornitore : {message?.cliente?.nome_cliente} <br/>
        -Prodotto : {message?.nome_prodotto} <br/>
        -Prezzo : {message?.prezzo} <br/>
        -Fattura : {message?.cliente?.fattura?.documento} <br/>
        -Acquisti : {num}
      </p>


      <div class="py-6"></div>

      <button class="w-36 bg-cyan-900 text-white text-xl" onClick={count} >Aggiungi</button>

      <div class="py-6"></div>

      <button class="w-36 bg-cyan-900 text-white text-xl" onClick={compra} >Compra</button>

      <p class="text-xl">
        Totale speso : {costo}
      </p>

      </header>

    </div>
  );
}

export default App;
