import { useState , useEffect } from "react";



function Download(){

    const files = [
        { id: 1, titolo: 'Documento1.pdf' },
        { id: 2, titolo: 'Report_annuale.docx' },
        { id: 3, titolo: 'Immagine.png' },
    ];

    return(
       <div className="max-w-4xl mx-auto p-4 bg-gray-900 rounded-lg shadow-lg">
      <h2 className="text-2xl font-semibold text-white mb-6">I tuoi documenti</h2>
      <ul className="space-y-4">
        {files.map((file) => (
          <li
            key={file.id}
            className="flex items-center justify-between bg-gray-800 p-4 rounded-lg hover:bg-gray-700 transition"
          >
            <div className="flex items-center space-x-4">
              <svg
                className="w-8 h-8 text-indigo-400"
                fill="none"
                stroke="currentColor"
                strokeWidth={2}
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M12 4v16m8-8H4"
                />
              </svg>
              <span className="text-white font-medium truncate max-w-xs">{file.titolo}</span>
            </div>
            <button
              onClick={() => alert(`Scarica ${file.titolo}`)}
              className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg transition"
            >
              Scarica
            </button>
          </li>
        ))}
      </ul>
    </div>
    );

}

export default Download;
