import React, { useEffect, useState } from 'react';
import CircleIcon from '@mui/icons-material/Circle';
function TabToDo(){

    const [task , setTask] = useState([]);
    const [stato , setStato] = useState(null);

    useEffect(()=>{

        fetch('api/tasks/')
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualcosa e andato storto')
                }
                return res.json()
            }
        )
        .then(
            data => 
            {
                setTask(data)
              
            }
        )
        .catch(err => console.log(err.message))

    },[]);


    return(
        
        <div className="w-auto grid grid-cols-1 md:grid-cols-2 gap-4 md:grid-rows-1  items-center content-center justify-center">

            <div className="bg-black h-16"></div>

                {Array.isArray(task) && task.map(task=>(
                
                    <div key={task.id} className="px-4 py-10 w-96 bg-slate-300 h-auto rounded-md  items-center content-center justify-center">
                        
                        <div className=''>

                            <div className='flex flex-col items-center'>
                                <div><h1>Titolo :{task.titolo} </h1></div>
                            </div>
                            

                        </div>
                        


                    </div>




                ))}    
           



            
            


        </div>
    );
}

export default TabToDo;
