import React, { useEffect, useState } from 'react';




function Shop(){

    const [content , setContent] = useState(null)

    useEffect(()=>{

        fetch('/api/note/')
        .then(res => res.json())
        .then(data => setContent(data))
        .catch(err =>  setContent([]));

    },[]);




    return(
        <div class="bg-slate-800">
            
            <div class="text-center py-24">
                <h1 class="text-2xl text-white  font-light">Practice 2</h1>
                
                <p class="text-white">
                    
                    {content.length === 0 ? (
                        <p>Nessuna nota trovata.</p>
                    ) : (
                        content.map((nota, idx) => (
                            <div key={idx}>
                                <strong>Titolo:</strong> {nota.titolo} <br />
                                <strong>Contenuto:</strong> {nota.contenuto}
                                <hr />
                            </div>
                        ))
                    )}



                    Titolo : {content.titolo } <br />
                    Contenuto : {content.contenuto} 
                </p>
               
            </div>
     
        </div>
    )

}

export default Shop;
