import React, { useEffect, useState } from 'react';



function Shop(){

    const [content , setContent] = useState([])

    const [id_note , setIdNote] = useState(0)
    const [titolo , setTitolo] = useState('')
    const [contenuto , setContenuto] = useState('')


    useEffect(()=>{

        fetch('/api/note/')
        .then(res => res.json())
        .then(data => setContent(data))  
        .catch(err =>  setContent([]));

    },[]);


    function creaNota(e){

        e.preventDefault();

        fetch
        (
            '/api/createNote/' , 
            {
                method : 'POST' , 
                headers: {'Content-Type' : 'application/json'},
                body : JSON.stringify({id_note,titolo,contenuto}), 
            }
        )
        .then(res => 
            {
                if (!res.ok){
                    throw new Error('Errore nella creazione della nota')
                }
                return res.json
            }
        )
        .then(
            
            data =>{setIdNote(0),setTitolo(''),setContenuto('')}
        
        )
    }



    return(

        <div class="bg-slate-800">
            
            <div class="text-center py-24">
                
                <h1 class="text-2xl text-white  font-light">Practice 2</h1>
                
                <p class="text-white">

                    {content.map(content => ( <li key={content.id_note}>{content.titolo} ----- {content.contenuto}</li>))} 

                </p>
               
            </div>

        </div>
    )
}

export default Shop;
