import React, { useEffect, useState } from 'react';
import CircleIcon from '@mui/icons-material/Circle';
function TabToDo(){

    const [task , setTask] = useState([]);
    const [stato , setStato] = useState(null);

    useEffect(()=>{

        fetch('api/tasks/')
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualcosa e andato storto')
                }
                return res.json()
            }
        )
        .then(
            data => 
            {
                setTask(data)
              
            }
        )
        .catch(err => console.log(err.message))

    },[]);


    return(
        
        <div className="w-auto">

            <div className="bg-black h-16"></div>
            <div className="text-6xl font-thin text-white text-center py-10 "><h1>OVERVIEW</h1></div>

            <div className="px-4 py-10 rounded-md">
                

                
                <table className=" w-full  border-blue-600">
                
                    <thead className="bg-gray-900  ">
                    
                        <tr>
                            
                            <th className="text-white font-light text-2xl py-5">TASK</th>
                            <th className="text-white font-light text-2xl py-5">CONTENUTO</th>
                            <th className="text-white font-light text-2xl py-5">SCADENZA</th>
                            <th className="text-white font-light text-2xl py-5">STATO</th>

                        </tr>
                    
                    </thead>


                    {Array.isArray(task) && task.map(task=>(

                        <tbody className="bg-slate-950 text-xl font-thin hover:bg-blue-400 " >
                                        
                            <tr key={task.id_task}  className="text-white text-center">
                                                    
                                <td className='py-4 hover:text-black'>{task.titolo}</td>
                                <td className='py-4 hover:text-black'>{task.contenuto}</td>
                                <td className='py-4 hover:text-black'>{task.data_formattata}</td>
                                <td className='py-4 hover:text-black'>{task.stato ? <CircleIcon sx={{color:'green'}}/> : <CircleIcon sx={{color:'red'}}/> }</td>
                                                
                            </tr>                        
                                        
                        </tbody>

                    ))}
                    


                    

                </table>

            </div>
            


        </div>
    );
}

export default TabToDo;
