import React, { useEffect, useState } from 'react';



function Note(){

    const [nota , setContent] = useState([])

   // const [id_note , setIdNote] = useState(0)
    const [titolo , setTitolo] = useState('')
    const [contenuto , setContenuto] = useState('')
    const [message , setMessage] = useState('')

    useEffect(()=>{

        fetch('/api/note/')
        .then(res => res.json())
        .then(data =>{
             console.log(data); 
             setContent(data); 
            }
        )  
        .catch(err =>  setContent([])); //importante quando un api restituisce molti oggetti e non solo uno essi si formattano ad array

    },[]);


    function testPUT(e){

        e.preventDefault()

        fetch
        (
            'api/createNote/',
            {
                method:'POST',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify({titolo , contenuto})
            }

        )
        .then(res =>

            {
                if(!res.ok){
                    throw new Error('Errore nella richiesta')
                }
                return res.json()
            }

        )
        .then(data=>
            {
                
                setTitolo('');
                setContent('');
                window.location.reload()
            }

        )
        .catch(err=> setMessage(err.message))
    }



    return(

        <div className="bg-slate-800">
            
            <div className="text-center py-24">
                
                <h1 className="text-2xl text-white  font-light">Practice 2</h1>
                
                

                {Array.isArray(nota) && nota.map(nota => ( 
                        
                    <li key={nota.id_note}>
                        
                        <ul>
                            {nota.id_note} -
                            {nota.titolo} -
                            {nota.contenuto} 


                        </ul>
                            
                    </li>
                        
                ))} 

                <div className='grid grid-cols-3 grid-rows-3 gap-4'>  
                
                    {Array.isArray(nota) && nota.map(nota => (
                
                        <div key={nota.id_note} className='bg-white rounded-md'>
                                
                            <h2>{nota.titolo}</h2>
                            <p>{nota.contenuto}</p>
                        </div>

                    ))}
                </div>
                
            </div>

        
            <div className='text-center text-white py-28'>
                <form onSubmit={testPUT}>
                    
                    <p>Titolo : </p>
                    <input className='text-black' type="text" value={titolo}  onChange={e => setTitolo(e.target.value)}/>
                    
                    <p>Contenuto : </p>
                    <input className='w-72 text-black' type="text" value={contenuto} onChange={e => setContenuto(e.target.value)} />
                    
                    <br />

                    <div className='py-5'></div>

                    <button className='bg-cyan-700 w-72' type='submit' >Crea la nota</button> 
                    {message && <p>{message}</p>}
                
                </form>

            </div>

        </div>
    )
}

export default Note;
