import React, { use, useEffect, useState } from 'react';
import 'reactjs-popup/dist/index.css';
import Popup from 'reactjs-popup';


function SendProduct(){

    const [showAlert, setShowAlert] = useState(false);

    const [prodotti , setProdotti] = useState('');

    const [nomeProd , setNomeProd] = useState('');
    const [descrizione , setDescrizione] = useState('');
    const [qta , setQta] = useState(0);
    const [prezzo , setPrezzo] = useState(0);
    const [fornitore , setFornitore] = useState(0);


    const [id_prod , setIdProd] = useState(0);

    //CREA NOMI DI VARIABILI DIVERSE FANNO CONFILITTO CON IL FORM GIA ESISTETE PER LA PATCH
    
   

    const [nome_prodotto , setNomeProdotto] = useState('');
    const [descrizione_prdotto , setDescrizioneProdotto] = useState('');
    const [qta_prodotto , setQtaProdotto] = useState(0);
    const [prezzo_prodotto , setPrezzoProdotto] = useState(0);
    const [id_fornitore , setIdFornitore] = useState(0);

    const payload = {};

    if(nome_prodotto.trim() !== '' )payload.nome_prodotto  = nome_prodotto
    if(descrizione_prdotto.trim() !== '')payload.descrizione = descrizione_prdotto
    if(qta_prodotto !== 0)payload.qta = qta_prodotto
    if (prezzo_prodotto !== 0) payload.prezzo = prezzo_prodotto
    if(id_fornitore !== 0)payload.cliente = id_fornitore


    function patchProd(e){
        
        e.preventDefault()
        
        fetch
        (
            `api/patch_prod/${id_prod}/`,

            {
                method:'PATCH',
                headers : {'Content-Type' : 'application/json'},
                body : JSON.stringify(payload)
            }
        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('errore durate l aggiornamento del prodotto')
                }
                return res.json()
            }
        )
        .then(data => {
            

            fetch('api/view_all_products/')
            .then(res => res.json())
            .then(data => setProdotti(data))
            .catch(err => setProdotti([]))
            
            setIdProd(0);
            setNomeProdotto('');
            setPrezzoProdotto(0);
            setDescrizioneProdotto('');
            setQtaProdotto(0);
            setIdFornitore(0);

            window.location.reload()

            console.log(data)
        })
        .catch(err => console.log(err))

    }



    function handleSend(e) {
    e.preventDefault();

    fetch('api/insert_product/', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            nome_prodotto: nomeProd,
            descrizione,
            qta,
            prezzo,
            cliente: fornitore,
        })
    })
    .then(res => {
        if (!res.ok) {
            throw new Error('Errore nel server: ' + res.status);
        }
        return res.json();
    })
    .then(data => {
        console.log('Response dal backend:', data);

        setNomeProd('');
        setDescrizione('');
        setQta(0);
        setPrezzo(0);
        setFornitore(0);

        alert('Prodotto inserito');
        window.location.reload();
    })
    .catch(err => {
        setShowAlert(err.message);
        console.error('Errore fetch:', err);
    });
}


    useEffect(() =>{

        fetch('api/view_all_products/')
        .then(res => res.json())
        .then(data => setProdotti(data))
        .catch(err => setProdotti([]))

    },[])


    function dropAll(){

        fetch
        (
            'api/delete_all_prod/',

            {
                method:'DELETE'
            }

        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualcosa e andato storto nella cancellazione dei dati')
                }
                return res.json()
               
            }
        )
        .catch(err => console.log(err))


        window.location.reload()

    }


    function deleteSelectedProd(){

        fetch
        (
            `api/delete_selected_product/${id_prod}/`,
            {

                method : 'DELETE'

            }
        )
        .then(res => 
            {
                if(!res.ok){
                    throw new Error('Qualocsa e andato storto durante la cancellazione del prodotto selezionato')
                }
            }
        )
        .catch(err => console.log(err))

        window.location.reload()
    }


    return(
        <div>
 
  

            <form  onSubmit={handleSend}>


                <div className='text-center  grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center py-36 bg-gray-900 rounded-md'>


                    <div className=''>
                       
                       <div className='text-left'>
                            <label>Prodotto</label>
                        </div>
                        
                        <input type="text" className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none'   placeholder='Nome prodotto' value={nomeProd} onChange={e => setNomeProd(e.target.value)}/>
                    </div>
                    
                    <div className=''>
                        
                        <div className='text-left'>
                            <label>Descrizione</label>
                        </div>
                        
                        <input type="text" className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' placeholder='Descrizione' value={descrizione} onChange={e => setDescrizione(e.target.value)}/>   
                    </div>
                    
                    <div className=''>
                        
                        <div className='text-left'>
                            <label>Quantita</label>
                        </div>
                       
                       <input type="number" className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' placeholder='quantita' value={qta} onChange={e => setQta(Number(e.target.value))}/>
                    
                    </div>

                            
                    <div className=''>

                        <div className='text-left'>
                            <label>Prezzo</label>
                        </div>

                      <input type="number" step="0.01" className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' placeholder='Prezzo' value={prezzo} onChange={e => setPrezzo(Number(e.target.value))}/>
                    </div>

                    
                    <div className=''>

                        <div className='text-left'>
                            <label>ID Fornitore</label>
                        </div>

                        <input type="number" className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' placeholder='Fornitore' value={fornitore} onChange={e => setFornitore(Number(e.target.value))}/>
                    </div>
                    

                    
                    <div className=' font-light'>
                        <button className='w-96 h-10 bg-green-500  rounded-md hover:bg-green-600' type="submit">Invio</button>
                    </div>

                </div>
                

            </form>
            
            <div className='py-10'></div>

            <table className='bg-gray-900 w-full rounded-md'>
                <thead>
                    <tr>

                        <th className='text-green-400'>ID</th>
                        <th className='text-green-400'>NOME</th>
                        <th className='text-green-400'>DESCRIZIONE</th>
                        <th className='text-green-400'>QUANTITA</th>
                        <th className='text-green-400'>PREZZO</th>
                        <th className='text-green-400'>FORNITORE</th>

                    </tr>
                </thead>

                <tbody >

                    {Array.isArray(prodotti) && prodotti.map(prodotti => (
                        <tr key={prodotti.id_prod} className='hover:bg-green-800'>
                            <td>{prodotti.id_prod}</td>
                            <td>{prodotti.nome_prodotto}</td>
                            <td>{prodotti.descrizione}</td>
                            <td>{prodotti.qta}</td>
                            <td>{prodotti.prezzo}$</td>
                            <td>{prodotti?.cliente?.societa?.nome_societa}</td>
                        </tr>
                    ))}
                    
                </tbody>

            </table>
            

            <div className='py-10 grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center'>
                
                <div>
                    
                    <Popup trigger=
                            
                            {<button className='bg-red-500 w-96 rounded-md h-10 font-bold hover:bg-red-600' >Drop All</button>}
                            
                            modal
                            nested
                            
                            position="right center"
                            contentStyle={{
                                background: 'transparent',     
                                border: 'none',                
                                boxShadow: 'none',             
                                padding: '0px'                 
                            }}
                                        
                        >
                            <h1 className='text-2xl text-white text-center'> Are you sure</h1>
                            
                            <div className='w-full grid grid-cols-1 md:grid-cols-2 gap-4 justify-items-center rounded-md py-10 bg-gray-900'>
                               

                                <button className='w-96 h-10 bg-red-500  rounded-md hover:bg-red-600' onClick={dropAll}>Yes</button>
                                <button className='w-96 h-10 bg-green-500  rounded-md hover:bg-green-600'>No</button>
                            </div>
                            
                        
                    </Popup>
                    

                </div>


                
                <div>
                
                    <Popup trigger=
                        
                        {<button className='bg-red-500 w-96 rounded-md h-10 font-bold hover:bg-red-600 '> Selected Drop </button>}
                        
                        modal
                        nested
                        
                        position="right center"
                        contentStyle={{
                            background: 'transparent',   
                            border: 'none',              
                            boxShadow: 'none',            
                            padding: '0px'               
                        }}
                                    
                    >

                        <div className='w-full grid grid-cols-1 md:grid-cols-2 gap-4 justify-items-center rounded-md py-10 bg-gray-900'>
                            
                            <input type="number" placeholder='ID PRODOTTO' className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none'
                            value={id_prod} onChange={e => setIdProd(Number(e.target.value))} />

                            <button className='w-96 h-10 bg-green-500  rounded-md hover:bg-green-600' onClick={deleteSelectedProd}>Confirm</button>
                        </div>
                        
                       
                    </Popup>

                </div>


                
                <div>
                
                    <Popup trigger=
                        
                        {<button className='bg-yellow-600 w-96 rounded-md h-10 font-bold hover:bg-yellow-500'> Modify </button>}
                        
                        modal
                        nested
                        
                        position="right center"
                        contentStyle={{
                            background: 'transparent',    
                            border: 'none',                
                            boxShadow: 'none',             
                            padding: '0px'               
                        }}
                                    
                    >   
                        <div className='text-center py-4'>
                            <h2 className='text-xl text-white'>Campi modificabili</h2>

                        </div>
                        

                        <div className=' rounded-md py-10 bg-gray-900'>
                            
                            
                            <form  onSubmit={patchProd}>

                                <div  className='text-center  grid grid-cols-1 md:grid-cols-3 gap-4 justify-items-center items-center'>
                                    <h1 className='text-white text-l'>Seleziona il prodotto da modificare in base al suo id</h1>
                                    <div>
                                        
                                        <input type="number" placeholder='ID Prodotto' value={id_prod} onChange={e=> setIdProd(Number(e.target.value))} className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' />

                                    </div>


                                    <div>
                                        <input type="number" placeholder='ID Fornitore' value={id_fornitore} onChange={e => setIdFornitore(Number(e.target.value))} className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' />

                                    </div>
                                    
                                    <div>
                                        <input type="text" placeholder='Nome prodotto' value={nome_prodotto} onChange={e => setNomeProdotto(e.target.value)} className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' />

                                    </div>

                                    <div>

                                        <input type="text" placeholder='Descrizione' value={descrizione_prdotto} onChange={e => setDescrizioneProdotto(e.target.value)} className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' /> 

                                    </div>
                                
                                    <div>
                                        <input type="number" placeholder='Quantita' value={qta_prodotto} onChange={e => setQtaProdotto(Number(e.target.value))} className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' />

                                    </div>

                                    <div>
                                        <input type="number" step="0.01" placeholder='Prezzo' value={prezzo_prodotto} onChange={e => setPrezzoProdotto(Number(e.target.value))} className='text-white rounded-md w-96 font-thin text-l h-10 bg-blue-600 px-3 py-3  border-none' />

                                    </div>
                                    
                                    <div>
                                        <button type='submit' className='w-96 h-10 bg-green-500  rounded-md hover:bg-green-600'>Confirm</button>
                                    </div>

                                </div>
                               
                            </form>
                           
                            
                            
                            
                           
                        </div>
                         
                       
                    </Popup>

                </div>




            </div>
         
       


        </div>
    );

}

export default SendProduct;
